'use strict';

module.exports = function (grunt) {

    // Please see the Grunt documentation for more information regarding task
    // creation: http://gruntjs.com/creating-tasks

    var n2a = require('native2ascii').native2ascii;

    grunt.registerMultiTask('native2ascii', 'a function similar to the native2ascii task of ant.', function () {
        // Merge task-specific and/or target-specific options with these defaults.
        var options = this.options({
            punctuation: '.',
            separator: ', '
        });

        // Iterate over all specified file groups.
        this.files.forEach(function (currentFile) {

            var src = currentFile.src.filter(function (filepath) {
                if (!grunt.file.exists(filepath)) {
                    grunt.log.warn('Source file "' + filepath + '" not found.');
                    return false;
                } else {
                    return true;
                }
            }).map(function (filepath) {
                    // Read file source.
                    return n2a(grunt.file.read(filepath))
                });

            // Write the destination file.
            grunt.file.write(currentFile.dest, src);

            // Print a success message.
            grunt.log.writeln('File "' + currentFile.dest + '" created.');
        });
    });

};
